// 53. Maximum Subarray
// https://leetcode.com/problems/maximum-subarray/

#include <limits.h>
#include <stdio.h>

int maxSubArray(int* nums, int numsSize) {
    if (numsSize == 0) return 0;
    int maxSum = INT_MIN;
    for (int i = 0; i < numsSize; ++i) {
        int acc = 0;
        for (int j = i; j < numsSize; ++j) {
            acc += nums[j];
            if (acc > maxSum) maxSum = acc;
        }
    }
    return maxSum;
}

